import React, { useState } from 'react';
import { ChevronDown, ChevronUp } from 'lucide-react';
import ClockFace from './clock/ClockFace';

const RetinalCalculator = () => {
    const [age, setAge] = useState('');
    const [pvrGrade, setPvrGrade] = useState('none');
    const [vitrectomyGauge, setVitrectomyGauge] = useState('25g');
    const [selectedHours, setSelectedHours] = useState([]);
    const [detachmentSegments, setDetachmentSegments] = useState([]);
    const [hoveredHour, setHoveredHour] = useState(null);
    const [showMath, setShowMath] = useState(false);

    // Updated coefficients with correct gauge values
    const coefficients = {
        constant: -1.611,

        age_group: {
            "45_to_64": 0.0,    // Reference category
            "65_to_79": 0.236,
            "80_plus": 0.498,
            "under_45": 0.459
        },

        break_location: {
            "9_to_3": 0.0,      // Reference category
            "4_or_8": 0.428,
            "5_to_7": 0.607,
            "no_break": 0.676
        },

        inferior_detachment: {
            "less_than_3": 0.0,  // Reference category
            "3_to_5": 0.441,
            "6_hours": 0.435
        },

        total_rd: {
            "no": 0.0,          // Reference category
            "yes": 0.663
        },

        pvr_grade: {
            "none_A_B": 0.0,    // Reference category
            "C": 0.220
        },

        gauge: {              // Corrected coefficients
            "20g": 0.0,         // Reference category
            "23g": -0.408,
            "25g": -0.885,
            "27g": -0.703,
            "not_recorded": -0.738
        }
    };

    const getAgeGroup = (age) => {
        if (!age) return "45_to_64"; // default to reference category
        const ageNum = parseInt(age);
        if (ageNum < 45) return "under_45";
        if (ageNum >= 80) return "80_plus";
        if (ageNum >= 65) return "65_to_79";
        return "45_to_64";
    };

    const getBreakLocation = () => {
        if (selectedHours.length === 0) return "no_break";

        const has4or8 = selectedHours.includes(4) || selectedHours.includes(8);
        const has5to7 = selectedHours.includes(5) || selectedHours.includes(6) || selectedHours.includes(7);

        if (has5to7) return "5_to_7";  // 5-7 takes precedence
        if (has4or8) return "4_or_8";
        return "9_to_3";  // reference category
    };

    const getInferiorDetachment = () => {
        const affectedHours = Array.from(new Set(detachmentSegments.map(seg =>
            Math.floor(seg / 5) + 1
        )));

        const has6 = affectedHours.includes(6);
        const has3to5 = [3, 4, 5].some(h => affectedHours.includes(h));

        if (has6) return "6_hours";
        if (has3to5) return "3_to_5";
        return "less_than_3";  // reference category
    };

    const isTotalRD = () => {
        const affectedHours = Array.from(new Set(detachmentSegments.map(seg =>
            Math.floor(seg / 5) + 1
        )));
        return affectedHours.length >= 10 ? "yes" : "no";
    };

    const pvrOptions = [
        { value: 'none', label: 'No PVR' },
        { value: 'A', label: 'Grade A' },
        { value: 'B', label: 'Grade B' },
        { value: 'C', label: 'Grade C' }
    ];

    const gaugeOptions = [
        { value: '20g', label: '20 gauge' },
        { value: '23g', label: '23 gauge' },
        { value: '25g', label: '25 gauge' },
        { value: '27g', label: '27 gauge' },
        { value: 'not_recorded', label: 'Not recorded' }
    ];

    // Helper function to determine PVR coefficient
    const getPVRGrade = () => {
        return pvrGrade === 'C' ? "C" : "none_A_B";
    };

    const calculateRiskWithSteps = () => {
        const steps = [];
        let logit = coefficients.constant;
        steps.push({
            label: "Constant",
            value: coefficients.constant.toFixed(3)
        });

        // Age coefficient
        const ageGroup = getAgeGroup(age);
        const ageCoef = coefficients.age_group[ageGroup];
        logit += ageCoef;
        steps.push({
            label: "Age group",
            value: ageCoef.toFixed(3),
            detail: `(${ageGroup.replace(/_/g, ' ')})`
        });

        // Break location coefficient
        const breakLoc = getBreakLocation();
        const breakCoef = coefficients.break_location[breakLoc];
        logit += breakCoef;
        steps.push({
            label: "Break location",
            value: breakCoef.toFixed(3),
            detail: `(${breakLoc.replace(/_/g, ' ')} o'clock)`
        });

        // RD location coefficients
        const totalRD = isTotalRD();
        if (totalRD === "yes") {
            const rdCoef = coefficients.total_rd.yes;
            logit += rdCoef;
            steps.push({
                label: "Total RD",
                value: rdCoef.toFixed(3)
            });
        } else {
            const infDet = getInferiorDetachment();
            const infDetCoef = coefficients.inferior_detachment[infDet];
            logit += infDetCoef;
            steps.push({
                label: "Inferior detachment",
                value: infDetCoef.toFixed(3),
                detail: `(${infDet.replace(/_/g, ' ')} o'clock)`
            });
        }

        // PVR grade coefficient
        const pvrType = getPVRGrade();
        const pvrCoef = coefficients.pvr_grade[pvrType];
        logit += pvrCoef;
        steps.push({
            label: "PVR grade",
            value: pvrCoef.toFixed(3),
            detail: `(grade ${pvrType === "C" ? "C" : "A/B"})`
        });

        // Vitrectomy gauge coefficient
        const gaugeCoef = coefficients.gauge[vitrectomyGauge.toLowerCase()];
        logit += gaugeCoef;
        steps.push({
            label: "Vitrectomy gauge",
            value: gaugeCoef.toFixed(3),
            detail: `(${vitrectomyGauge}, odds ratio ${Math.exp(gaugeCoef).toFixed(3)})`
        });

        // Final calculations
        const probability = 1 / (1 + Math.exp(-logit));

        return {
            steps,
            logit: logit.toFixed(3),
            probability: (probability * 100).toFixed(1)
        };
    };

    const handleClockChange = ({ tears, detachment }) => {
        setSelectedHours(tears);
        setDetachmentSegments(detachment);
    };

    const handleHoverChange = (hour) => {
        setHoveredHour(hour);
    };

    const handleTearToggle = (hour) => {
        const newSelection = selectedHours.includes(hour)
            ? selectedHours.filter(h => h !== hour)
            : [...selectedHours, hour];
        setSelectedHours(newSelection);
        handleClockChange({ tears: newSelection, detachment: detachmentSegments });
    };

    const handleSegmentToggle = (segment) => {
        const newDetachment = detachmentSegments.includes(segment)
            ? detachmentSegments.filter(s => s !== segment)
            : [...detachmentSegments, segment];
        setDetachmentSegments(newDetachment);
        handleClockChange({ tears: selectedHours, detachment: newDetachment });
    };

    const risk = calculateRiskWithSteps();

    return (
        <div className="flex flex-col space-y-4 p-4">
            <div className="bg-white rounded-lg shadow-lg">
                <div className="p-6">
                    <h2 className="text-2xl font-bold mb-2">Retinal Detachment Risk Calculator</h2>
                    <p className="text-sm text-gray-600 mb-6">
                        Based on the UK BEAVRS database study of retinal detachment outcomes
                    </p>

                    <div className="space-y-6">
                        {/* Age input */}
                        <div className="space-y-2">
                            <label className="block text-sm font-medium text-gray-700">Age (years)</label>
                            <input
                                type="number"
                                className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                value={age}
                                onChange={(e) => setAge(e.target.value)}
                                min="0"
                                max="120"
                                placeholder="Enter age"
                            />
                        </div>

                        {/* PVR Grade radio group */}
                        <div className="space-y-2">
                            <label className="block text-sm font-medium text-gray-700">PVR Grade</label>
                            <div className="grid grid-cols-2 gap-4 sm:grid-cols-4">
                                {pvrOptions.map((option) => (
                                    <label key={option.value} className="flex items-center space-x-2">
                                        <input
                                            type="radio"
                                            name="pvr"
                                            value={option.value}
                                            checked={pvrGrade === option.value}
                                            onChange={(e) => setPvrGrade(e.target.value)}
                                            className="h-4 w-4 text-blue-600 focus:ring-blue-500"
                                        />
                                        <span className="text-sm text-gray-700">{option.label}</span>
                                    </label>
                                ))}
                            </div>
                        </div>

                        {/* Vitrectomy Gauge radio group */}
                        <div className="space-y-2">
                            <label className="block text-sm font-medium text-gray-700">Vitrectomy Gauge</label>
                            <div className="grid grid-cols-2 gap-4 sm:grid-cols-3">
                                {gaugeOptions.map((option) => (
                                    <label key={option.value} className="flex items-center space-x-2">
                                        <input
                                            type="radio"
                                            name="gauge"
                                            value={option.value}
                                            checked={vitrectomyGauge === option.value}
                                            onChange={(e) => setVitrectomyGauge(e.target.value)}
                                            className="h-4 w-4 text-blue-600 focus:ring-blue-500"
                                        />
                                        <span className="text-sm text-gray-700">{option.label}</span>
                                    </label>
                                ))}
                            </div>
                        </div>

                        {/* Clock Face */}
                        <div className="mt-4">
                            <ClockFace
                                selectedHours={selectedHours}
                                detachmentSegments={detachmentSegments}
                                hoveredHour={hoveredHour}
                                onChange={handleClockChange}
                                onHoverChange={handleHoverChange}
                                onTearToggle={handleTearToggle}
                                onSegmentToggle={handleSegmentToggle}
                            />
                        </div>

                        {/* Results section */}
                        <div className="mt-6 space-y-4">
                            <div className="p-4 bg-gray-50 rounded-lg">
                                <p className="text-lg font-semibold">
                                    Estimated Risk of Failure: {risk.probability}%
                                </p>
                            </div>

                            <div className="border rounded-lg overflow-hidden">
                                <button
                                    onClick={() => setShowMath(!showMath)}
                                    className="w-full p-4 text-left bg-white hover:bg-gray-50 flex justify-between items-center"
                                >
                                    <span className="font-medium">Show calculation steps</span>
                                    {showMath ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
                                </button>

                                {showMath && (
                                    <div className="p-4 bg-white border-t">
                                        <div className="space-y-2">
                                            {risk.steps.map((step, index) => (
                                                <div key={index} className="flex items-baseline">
                                                    <span className="w-1/3 text-sm text-gray-600">{step.label}:</span>
                                                    <span className="w-20 font-mono">{step.value}</span>
                                                    {step.detail && (
                                                        <span className="text-sm text-gray-500 ml-2">{step.detail}</span>
                                                    )}
                                                </div>
                                            ))}
                                            <div className="mt-4 pt-4 border-t">
                                                <div className="flex items-baseline">
                                                    <span className="w-1/3 text-sm font-medium text-gray-600">Total logit:</span>
                                                    <span className="w-20 font-mono font-medium">{risk.logit}</span>
                                                </div>
                                                <div className="mt-2 text-sm text-gray-600">
                                                    Probability = 1 / (1 + e<sup>-logit</sup>) × 100%
                                                </div>
                                                <div className="mt-1 text-sm text-gray-600">
                                                    = 1 / (1 + e<sup>{-risk.logit}</sup>) × 100%
                                                </div>
                                                <div className="mt-1 text-sm font-medium text-gray-900">
                                                    = {risk.probability}%
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default RetinalCalculator;
