import React from 'react';
import Controls from './Controls';
import ClockFace from './ClockFace';
import { useClockInteractions } from './hooks/useClockInteractions';

const MinimalClockSelector = ({ onChange }) => {
  const {
    selectedHours,
    detachmentSegments,
    hoveredHour,
    setHoveredHour,
    isAddMode,
    setIsAddMode,
    isDrawing,
    isTouchDevice,
    handleTearTouchMove,
    handleTearTouchEnd,
    handleSegmentInteraction,
    handleStartDrawing,
    handleDrawing,
    handleMouseDown,
    handleTearClick,
    handleTearTouchStart,
    handleEndDrawing,
    handleClearAll
  } = useClockInteractions(onChange);

  const handlers = {
    setHoveredHour,
    handleTearTouchMove,
    handleTearTouchEnd,
    handleSegmentInteraction,
    handleStartDrawing,
    handleDrawing,
    handleMouseDown,
    handleTearClick,
    handleTearTouchStart,
    handleEndDrawing
  };

  return (
    <div className="p-4"> {/* This should add padding */}
      {/* Tailwind Test Section */}
      <div className="mb-4 p-4 bg-blue-100 rounded-lg">
        <h2 className="text-xl font-bold text-blue-800 mb-2">Tailwind Test</h2>
        <div className="flex gap-4 mb-4">
          <div className="bg-red-500 text-white p-2 rounded">Red Box</div>
          <div className="bg-green-500 text-white p-2 rounded">Green Box</div>
          <div className="bg-blue-500 text-white p-2 rounded">Blue Box</div>
        </div>
        <button className="bg-purple-500 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded">
          Test Button
        </button>
      </div>

      {/* Original Component */}
      <div className="flex flex-col md:flex-row md:items-start md:gap-8 max-w-6xl mx-auto">
        <div className="w-full md:w-64 mb-4 md:mb-0">
          <Controls
            isTouchDevice={isTouchDevice}
            isAddMode={isAddMode}
            setIsAddMode={setIsAddMode}
            isDrawing={isDrawing}
            handleClearAll={handleClearAll}
          />
        </div>

        <div className="flex-grow flex justify-center">
          <ClockFace
            selectedHours={selectedHours}
            detachmentSegments={detachmentSegments}
            hoveredHour={hoveredHour}
            isTouchDevice={isTouchDevice}
            handlers={handlers}
          />
        </div>
      </div>
    </div>
  );
};

export default MinimalClockSelector;
