import React, { useState } from 'react';
import RiskInputForm from './RiskInputForm';
import RiskResults from './RiskResults';
import { calculateRisk } from '../utils/riskCalculations';

const RetinalCalculator = () => {
  const [riskScore, setRiskScore] = useState(null);
  const [formData, setFormData] = useState({
    age: '',
    pvrGrade: 'none',
    vitrectomyGauge: '25',
    clockHours: 0,
    detachmentSegments: []
  });

  const handleCalculate = () => {
    const result = calculateRisk(formData);
    setRiskScore(result);
  };

  const handleReset = () => {
    setRiskScore(null);
    setFormData({
      age: '',
      pvrGrade: 'none',
      vitrectomyGauge: '25',
      clockHours: 0,
      detachmentSegments: []
    });
  };

  return (
    <div className="container mx-auto px-4 py-4 max-w-4xl">
      <h1 className="text-2xl font-bold text-center mb-4">
        Retinal Detachment Risk Calculator
      </h1>
      <p className="text-center text-gray-600 mb-6">
        Based on the UK BEAVRS database study of retinal detachment outcomes
      </p>

      {!riskScore ? (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <RiskInputForm
              position="left"
              formData={formData}
              setFormData={setFormData}
            />
            <RiskInputForm
              position="right"
              formData={formData}
              setFormData={setFormData}
            />
          </div>
          <button
            onClick={handleCalculate}
            disabled={!formData.age || formData.detachmentSegments.length === 0}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:bg-gray-400"
          >
            Calculate Risk
          </button>
        </div>
      ) : (
        <RiskResults 
          score={riskScore}
          formData={formData}
          onReset={handleReset}
        />
      )}
    </div>
  );
};

export default RetinalCalculator;
