import React, { useState, useRef, useCallback } from "react";

const ClockFace = ({
  selectedHours,
  detachmentSegments,
  hoveredHour,
  isTouchDevice,
  onSegmentToggle,
  onTearToggle,
  onHoverChange,
}) => {
  // Constants
  const outerRadius = 110;
  const innerRadius = 65;
  const middleRadius = Math.floor((outerRadius + innerRadius) / 2);
  const tearRadius = middleRadius + 12;
  const indicatorExtension = 10;

  // Style constants remain unchanged
  const styles = {
    transition: 'fill 0.2s ease, stroke 0.2s ease',
    tear: {
      default: {
        fill: '#dc2626',
        stroke: '#dc2626',
        strokeWidth: '0.5'
      },
      hover: {
        fill: '#b91c1c',
        stroke: '#b91c1c',
        strokeWidth: '0.5'
      }
    },
    circle: {
      default: {
        fill: 'white',
        stroke: '#d1d5db',
        strokeWidth: '1.5'
      },
      hover: {
        fill: '#fee2e2',
        stroke: '#d1d5db',
        strokeWidth: '1.5'
      }
    }
  };

  // State
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastPosition, setLastPosition] = useState(null);
  const svgRef = useRef(null);

  // Updated coordinate conversion functions
  const polarToCartesian = (angle, r) => {
    const radian = (angle - 90) * (Math.PI / 180); // -90 to start at top
    return {
      x: r * Math.cos(radian),
      y: r * Math.sin(radian)
    };
  };

  const cartesianToPolar = (x, y) => {
    const angle = Math.atan2(y, x) * (180 / Math.PI);
    return (90 - angle + 360) % 360; // Convert to clockwise from top
  };

  // Updated hour and position calculations
  const getPosition = (hour, radius) => {
    const angle = hour * 30; // 360/12 = 30 degrees per hour
    const point = polarToCartesian(angle, radius);
    return {
      ...point,
      angle,
      debug: {
        hour,
        clockAngle: angle
      }
    };
  };

  // Rest of the utility functions remain unchanged
  const degreeToSegment = (degree) => {
    return Math.floor(degree / 6) % 60;
  };

  const segmentToDegree = (segment) => {
    return (segment * 6) % 360;
  };

  // Convert segment number (0-59) to clock hour (1-12)
  const segmentToClockHour = (segment) => {
    // Each hour spans 5 segments (60 segments / 12 hours)
    const hour = Math.floor(segment / 5) + 1;
    return hour > 12 ? hour - 12 : hour;
  };

  // Group consecutive numbers into ranges
  const groupConsecutive = (numbers) => {
    if (numbers.length === 0) return [];
    const sorted = [...new Set(numbers)].sort((a, b) => a - b);
    const ranges = [];
    let range = [sorted[0]];

    for (let i = 1; i < sorted.length; i++) {
      const curr = sorted[i];
      const prev = sorted[i - 1];

      // Check if numbers are consecutive (including 12->1 wrap)
      if (curr === prev + 1 || (prev === 12 && curr === 1)) {
        range.push(curr);
      } else {
        ranges.push(range);
        range = [curr];
      }
    }
    ranges.push(range);
    return ranges;
  };

  // Format a range of hours into a string
  const formatHourRange = (range) => {
    if (range.length === 1) return `${range[0]}`;
    return `${range[0]}-${range[range.length - 1]}`;
  };

  // Create description of current selections
  const createSelectionDescription = () => {
    // Handle tears (already in clock hours)
    const tearRanges = groupConsecutive(selectedHours);
    const tearDescription = tearRanges.length > 0
      ? `Breaks at ${tearRanges.map(formatHourRange).join(', ')} o'clock`
      : 'No breaks marked';

    // Handle detachment (convert segments to clock hours first)
    const detachmentHours = [...new Set(detachmentSegments.map(segmentToClockHour))];
    const detachmentRanges = groupConsecutive(detachmentHours);
    const detachmentDescription = detachmentRanges.length > 0
      ? `Detachment from ${detachmentRanges.map(formatHourRange).join(', ')} o'clock`
      : 'No detachment marked';

    return { tearDescription, detachmentDescription };
  };  

  // Style helper remains unchanged
  const getStyles = (hour, hoveredHour, isSelected) => {
    const isHovered = hoveredHour === hour;
    if (isSelected) {
      return {
        ...styles.tear.default,
        ...(isHovered ? styles.tear.hover : {}),
        transition: styles.transition
      };
    }
    return {
      ...styles.circle.default,
      ...(isHovered ? styles.circle.hover : {}),
      transition: styles.transition
    };
  };

  // Create tear path remains unchanged
  const createTearPath = (x, y, angle) => {
    const tearPath = `
      M -4 -8
      c -0.091 -0.936 0.333 -1.232 0.777 0.658
      c 0.389 1.655 1.060 3.281 1.060 3.281
      s 0 0.254 1.022 0.617
      c 0.793 0.282 2.183 -2.882 2.183 -2.882
      s 1.953 -4.433 1.437 -1.294
      c -1.217 7.410 -1.640 6.716 -1.664 6.897
      c -0.024 0.181 -0.510 0.596 -0.510 0.596
      s -0.178 0.183 -0.585 0.327
      c -3.121 1.110 -3.163 -3.001 -3.163 -3.001
      L -4 -8
    `;
    return {
      d: tearPath,
      transform: `translate(${x}, ${y}) scale(1.5) rotate(${angle})`
    };
  };

  // Event handlers remain unchanged
  const getSegmentFromPoint = useCallback((clientX, clientY) => {
    const svgRect = svgRef.current.getBoundingClientRect();
    const centerX = svgRect.left + svgRect.width / 2;
    const centerY = svgRect.top + svgRect.height / 2;
    const relX = clientX - centerX;
    const relY = -(clientY - centerY);
    const angle = cartesianToPolar(relX, relY);
    return degreeToSegment(angle);
  }, []);

  const handleDrawingStart = useCallback((e) => {
    e.preventDefault();
    setIsDrawing(true);
    const touch = e.touches?.[0] || e;
    const segment = getSegmentFromPoint(touch.clientX, touch.clientY);
    setLastPosition(segment);
    onSegmentToggle(segment);
  }, [getSegmentFromPoint, onSegmentToggle]);

  const handleDrawing = useCallback((e) => {
    if (!isDrawing) return;
    e.preventDefault();
    const touch = e.touches?.[0] || e;
    const currentSegment = getSegmentFromPoint(touch.clientX, touch.clientY);
    if (currentSegment !== lastPosition) {
      onSegmentToggle(currentSegment);
      setLastPosition(currentSegment);
    }
  }, [isDrawing, lastPosition, getSegmentFromPoint, onSegmentToggle]);

  const handleDrawingEnd = useCallback((e) => {
    if (!isDrawing) return;
    e.preventDefault();
    setIsDrawing(false);
    setLastPosition(null);
  }, [isDrawing]);

  // Long press handler remains unchanged
  const handleTearLongPress = useCallback((hour) => {
    let timeout;
    const start = () => {
      timeout = setTimeout(() => {
        onTearToggle(hour);
      }, 500);
    };
    const cancel = () => {
      if (timeout) {
        clearTimeout(timeout);
      }
    };
    return {
      onMouseDown: start,
      onTouchStart: (e) => {
        e.preventDefault();
        start();
      },
      onMouseUp: cancel,
      onMouseLeave: cancel,
      onTouchEnd: cancel,
      onTouchCancel: cancel,
    };
  }, [onTearToggle]);

  return (
    <div className="flex flex-col items-center space-y-4">
      <div className="relative touch-none select-none"
        style={{
          width: "min(80vw, min(80vh, 500px))",
          aspectRatio: "1",
          minWidth: "200px",
          maxWidth: "500px",
        }}
        onContextMenu={(e) => e.preventDefault()}
      >
        {/* [Previous SVG code remains exactly the same] */}
        <svg>
          {/* ... all existing SVG content ... */}
        </svg>
      </div>

      {/* Add this new description panel */}
      <div className="w-full max-w-md bg-white rounded-lg shadow-sm p-4">
        <div className="space-y-2">
          <h3 className="text-sm font-medium text-gray-900">Current Selection:</h3>
          <div className="space-y-1">
            {(() => {
              const { tearDescription, detachmentDescription } = createSelectionDescription();
              return (
                <>
                  <p className="text-sm text-gray-600">{tearDescription}</p>
                  <p className="text-sm text-gray-600">{detachmentDescription}</p>
                </>
              );
            })()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClockFace;
