import React, { useState, useRef, useCallback } from "react";

import {
  getPosition,
  createTearPath,
} from "./utils/clockCalculations";
import { getStyles } from "./styles/clockStyles";
import CoordinateSystem from "./utils/clockCoordinates";

const ClockFace = ({
  selectedHours,
  detachmentSegments,
  hoveredHour,
  isTouchDevice,
  onSegmentToggle,
  onTearToggle,
  onHoverChange,
}) => {
  // Constants
  const outerRadius = 110;
  const innerRadius = 65;
  const middleRadius = Math.floor((outerRadius + innerRadius) / 2);
  const tearRadius = middleRadius + 12;
  const indicatorExtension = 10;

  // State
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastPosition, setLastPosition] = useState(null);
  const svgRef = useRef(null);

  // Segment calculation functions

  const getSegmentFromPoint = useCallback((x, y) => {
    const svgRect = svgRef.current.getBoundingClientRect();
    const centerX = svgRect.left + svgRect.width / 2;
    const centerY = svgRect.top + svgRect.height / 2;

    // Get coordinates relative to center
    const relX = x - centerX;
    const relY = -(y - centerY);  // Flip Y for SVG coordinate system

    console.log('Click coordinates:', {
      original: { x, y },
      relative: { x: relX, y: relY }
    });

    const segment = CoordinateSystem.getSegmentFromPoint(relX, relY);
    console.log('Calculated segment:', segment);
    return segment;
  }, []);

  // Drawing event handlers
  const handleDrawingStart = useCallback(
    (e) => {
      e.preventDefault();
      setIsDrawing(true);
      const touch = e.touches?.[0] || e;
      const segment = getSegmentFromPoint(touch.clientX, touch.clientY);
      console.log("Drawing Start:", { segment });
      setLastPosition(segment);
      onSegmentToggle(segment);
    },
    [getSegmentFromPoint, onSegmentToggle]
  );

  const handleDrawing = useCallback(
    (e) => {
      if (!isDrawing) return;
      e.preventDefault();

      const touch = e.touches?.[0] || e;
      const currentSegment = getSegmentFromPoint(touch.clientX, touch.clientY);

      if (currentSegment !== lastPosition) {
        console.log("Drawing Move:", { lastPosition, currentSegment });
        onSegmentToggle(currentSegment);
        setLastPosition(currentSegment);
      }
    },
    [isDrawing, lastPosition, getSegmentFromPoint, onSegmentToggle]
  );

  const handleDrawingEnd = useCallback(
    (e) => {
      if (!isDrawing) return;
      e.preventDefault();
      console.log("Drawing End");
      setIsDrawing(false);
      setLastPosition(null);
    },
    [isDrawing]
  );

  // Long press handler for tears
  const handleTearLongPress = useCallback(
    (hour) => {
      let timeout;

      const start = () => {
        timeout = setTimeout(() => {
          onTearToggle(hour);
        }, 500);
      };

      const cancel = () => {
        if (timeout) {
          clearTimeout(timeout);
        }
      };

      return {
        onMouseDown: start,
        onTouchStart: (e) => {
          e.preventDefault();
          start();
        },
        onMouseUp: cancel,
        onMouseLeave: cancel,
        onTouchEnd: cancel,
        onTouchCancel: cancel,
      };
    },
    [onTearToggle]
  );

  return (
    <div className="flex justify-center">
      <div
        className="relative touch-none select-none"
        style={{
          width: "min(80vw, min(80vh, 500px))",
          aspectRatio: "1",
          minWidth: "200px",
          maxWidth: "500px",
        }}
        onContextMenu={(e) => e.preventDefault()}
      >
        <svg
          ref={svgRef}
          viewBox="-110 -110 220 220"
          className="w-full h-full"
          preserveAspectRatio="xMidYMid meet"
          onMouseDown={handleDrawingStart}
          onMouseMove={handleDrawing}
          onMouseUp={handleDrawingEnd}
          onMouseLeave={handleDrawingEnd}
          onTouchStart={handleDrawingStart}
          onTouchMove={handleDrawing}
          onTouchEnd={handleDrawingEnd}
        >
          {/* Grid circles */}
          <circle
            cx="0"
            cy="0"
            r={outerRadius}
            fill="none"
            stroke="#e5e5e5"
            strokeWidth="1"
          />
          <circle
            cx="0"
            cy="0"
            r={middleRadius}
            fill="none"
            stroke="#e5e5e5"
            strokeWidth="0.5"
          />
          <circle
            cx="0"
            cy="0"
            r={innerRadius}
            fill="none"
            stroke="#e5e5e5"
            strokeWidth="1"
          />

          {/* Detachment segments */}
          <g>
  {[...Array(60)].map((_, i) => {
    console.log(`Rendering segment ${i}, colored: ${detachmentSegments.includes(i)}`);
    // Adjust index to match our coordinate system
    const visualIndex = (60 - i) % 60;
    const pos = CoordinateSystem.getPointFromSegment(visualIndex, innerRadius);
    const posOuter = CoordinateSystem.getPointFromSegment(visualIndex, outerRadius);
    const nextPos = CoordinateSystem.getPointFromSegment((visualIndex + 1) % 60, innerRadius);
    const nextPosOuter = CoordinateSystem.getPointFromSegment((visualIndex + 1) % 60, outerRadius);

    return (
      <path
        key={`segment-${i}`}
        d={`M ${pos.x} ${pos.y} 
            L ${posOuter.x} ${posOuter.y} 
            A ${outerRadius} ${outerRadius} 0 0 1 ${nextPosOuter.x} ${nextPosOuter.y}
            L ${nextPos.x} ${nextPos.y}
            A ${innerRadius} ${innerRadius} 0 0 0 ${pos.x} ${pos.y}`}
        fill={detachmentSegments.includes(i) ? "rgba(59, 130, 246, 0.5)" : "transparent"}
        className="cursor-pointer hover:fill-blue-200 transition-colors"
      />
    );
  })}
</g>

          {/* Debug numbers */}
          {[...Array(60)].map((_, i) => {
            const displayNumber = (30 - i + 60) % 60;
            const pos = CoordinateSystem.getPointFromSegment(i, (innerRadius + outerRadius) / 2);
            return (
              <text
                key={`debug-${i}`}
                x={pos.x}
                y={pos.y}
                textAnchor="middle"
                alignmentBaseline="middle"
                fontSize="6"
                fill="#666"
              >
                {displayNumber}
              </text>
            );
          })}

          {/* Tear markers */}
          <g>
            {[...Array(12)].map((_, i) => {
              const hour = i + 1;
              const visualPos = getPosition(hour, tearRadius);
              const interactionPos = isTouchDevice
                ? getPosition(hour, outerRadius)
                : visualPos;
              const isSelected = selectedHours.includes(hour);

              return (
                <g
                  key={`tear-${hour}`}
                  {...(isTouchDevice
                    ? handleTearLongPress(hour)
                    : { onClick: () => onTearToggle(hour) })}
                  onMouseEnter={() => onHoverChange(hour)}
                  onMouseLeave={() => onHoverChange(null)}
                  style={{ cursor: "pointer" }}
                  data-hour={hour}
                >
                  {isTouchDevice && (
                    <circle
                      cx={interactionPos.x}
                      cy={interactionPos.y}
                      r="12"
                      fill="transparent"
                    />
                  )}

                  {isSelected ? (
                    <path
                      {...createTearPath(
                        visualPos.x,
                        visualPos.y,
                        visualPos.angle
                      )}
                      style={getStyles(hour, hoveredHour, true)}
                    />
                  ) : (
                    <circle
                      cx={visualPos.x}
                      cy={visualPos.y}
                      r="12"
                      style={getStyles(hour, hoveredHour, false)}
                    />
                  )}
                </g>
              );
            })}
          </g>

          {/* 12 o'clock indicator */}
          <line
            x1="0"
            y1={-outerRadius}
            x2="0"
            y2={-(outerRadius + indicatorExtension)}
            stroke="#666"
            strokeWidth="2"
          />
        </svg>
      </div>
    </div>
  );
};

export default ClockFace;
