import { useEffect, useState } from 'react';
import { useMediaQuery } from '../../hooks/useMediaQuery';
import Segment from './Segment';

const ClockFace = ({ selectedSegments, onSegmentClick }) => {
  const isMobile = useMediaQuery('(max-width: 768px)');
  const segmentCount = isMobile ? 24 : 60;

  return (
    <div className="relative w-full h-full">
      <div className="absolute inset-0 flex items-center justify-center">
        <div className="relative w-full h-full">
          {Array.from({ length: segmentCount }, (_, index) => {
            const angle = (index * (360 / segmentCount));
            const logicIndex = isMobile ? index * (60/24) : index;

            return (
              <Segment
                key={index}
                angle={angle}
                isSelected={selectedSegments.includes(Math.floor(logicIndex))}
                onClick={() => onSegmentClick(Math.floor(logicIndex))}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default ClockFace;
