import React, { useState } from 'react';
import ClockFace from './clock/ClockFace';

const RetinalCalculator = () => {
  const [age, setAge] = useState('');
  const [pvrGrade, setPvrGrade] = useState('A');
  const [selectedHours, setSelectedHours] = useState([]);
  const [detachmentSegments, setDetachmentSegments] = useState([]);
  const [hoveredHour, setHoveredHour] = useState(null);

  // From the prototype, keeping consistent coefficient names
  const coefficients = {
    constant: -2.391,
    ageGroups: {
      'age 65 to 79': 0.841,
      'age ≥80': 1.137,
      'age <45': -0.841,
      'Other': 0
    },
    pvrGrade: {
      'C': 1.619,
      'A': 0,
      'B': 0
    },
    breakLocation: {
      four_eight: 0.405,
      five_seven: 0.619,
      none: 1.099
    },
    rdLocation: {
      three_five: 0.560,
      six: 0.993,
      total: 1.078
    }
  };

  const getAgeGroup = (age) => {
    if (!age) return 'Other';
    const ageNum = parseInt(age);
    if (ageNum < 45) return 'age <45';
    if (ageNum >= 80) return 'age ≥80';
    if (ageNum >= 65 && ageNum <= 79) return 'age 65 to 79';
    return 'Other';
  };

  // Break location analysis following prototype logic
  const analyzeBreakLocation = () => {
    if (selectedHours.length === 0) return coefficients.breakLocation.none;

    const has48 = selectedHours.includes(4) || selectedHours.includes(8);
    const has567 = selectedHours.includes(5) || selectedHours.includes(6) || selectedHours.includes(7);

    if (has48) return coefficients.breakLocation.four_eight;
    if (has567) return coefficients.breakLocation.five_seven;
    return 0;
  };

  // RD location analysis following prototype logic
  const analyzeRDLocation = () => {
    const affectedHours = Array.from(new Set(detachmentSegments.map(seg => 
      Math.floor(seg / 5) + 1
    )));

    const has345 = [3, 4, 5].some(h => affectedHours.includes(h));
    const has6 = affectedHours.includes(6);
    const isTotal = affectedHours.length >= 10;

    let coefficient = 0;
    if (isTotal) coefficient += coefficients.rdLocation.total;
    else {
      if (has345) coefficient += coefficients.rdLocation.three_five;
      if (has6) coefficient += coefficients.rdLocation.six;
    }
    return coefficient;
  };

  const calculateRisk = () => {
    // Start with the constant
    let logit = coefficients.constant;

    // Add exactly one age coefficient based on age group
    logit += coefficients.ageGroups[getAgeGroup(age)];

    // Add exactly one PVR coefficient based on grade
    logit += coefficients.pvrGrade[pvrGrade];

    // Add exactly one break location coefficient
    if (selectedHours.length === 0) {
        logit += coefficients.breakLocation.none;
    } else if (selectedHours.includes(4) || selectedHours.includes(8)) {
        logit += coefficients.breakLocation.four_eight;
    } else if (selectedHours.includes(5) || selectedHours.includes(6) || selectedHours.includes(7)) {
        logit += coefficients.breakLocation.five_seven;
    }

    // Add exactly one RD location coefficient
    const affectedHours = Array.from(new Set(detachmentSegments.map(seg => 
        Math.floor(seg / 5) + 1
    )));

    if (affectedHours.length >= 10) {
        logit += coefficients.rdLocation.total;
    } else if (affectedHours.includes(6)) {
        logit += coefficients.rdLocation.six;
    } else if ([3, 4, 5].some(h => affectedHours.includes(h))) {
        logit += coefficients.rdLocation.three_five;
    }

    // Convert logit to probability
    const probability = 1 / (1 + Math.exp(-logit));
    return (probability * 100).toFixed(1);
};
  const handleClockChange = ({ tears, detachment }) => {
    setSelectedHours(tears);
    setDetachmentSegments(detachment);
  };

  const handleHoverChange = (hour) => {
    setHoveredHour(hour);
  };

  const handleTearToggle = (hour) => {
    const newSelection = selectedHours.includes(hour)
      ? selectedHours.filter(h => h !== hour)
      : [...selectedHours, hour];
    setSelectedHours(newSelection);
    handleClockChange({ tears: newSelection, detachment: detachmentSegments });
  };

  const handleSegmentToggle = (segment) => {
    const newDetachment = detachmentSegments.includes(segment)
      ? detachmentSegments.filter(s => s !== segment)
      : [...detachmentSegments, segment];
    setDetachmentSegments(newDetachment);
    handleClockChange({ tears: selectedHours, detachment: newDetachment });
  };

  return (
    <div className="flex flex-col space-y-4 p-4">
      <div className="bg-white rounded-lg shadow-lg">
        <div className="p-6">
          <h2 className="text-2xl font-bold mb-2">Retinal Detachment Risk Calculator</h2>
          <p className="text-sm text-gray-600 mb-6">
            Based on the UK BEAVRS database study of retinal detachment outcomes
          </p>

          <div className="space-y-4">
            <div className="space-y-2">
              <label className="block text-sm font-medium text-gray-700">Age (years)</label>
              <input 
                type="number"
                className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                value={age}
                onChange={(e) => setAge(e.target.value)}
                min="0"
                max="120"
                placeholder="Enter age"
              />
            </div>

            <div className="space-y-2">
              <label className="block text-sm font-medium text-gray-700">PVR Grade</label>
              <select 
                className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                value={pvrGrade}
                onChange={(e) => setPvrGrade(e.target.value)}
              >
                <option value="A">Grade A</option>
                <option value="B">Grade B</option>
                <option value="C">Grade C</option>
              </select>
            </div>

            <div className="mt-4">
              <ClockFace
                selectedHours={selectedHours}
                detachmentSegments={detachmentSegments}
                hoveredHour={hoveredHour}
                onChange={handleClockChange}
                onHoverChange={handleHoverChange}
                onTearToggle={handleTearToggle}
                onSegmentToggle={handleSegmentToggle}
              />
            </div>

            <div className="mt-6 p-4 bg-gray-50 rounded-lg">
              <p className="text-lg font-semibold">
                Estimated Risk of Failure: {calculateRisk()}%
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RetinalCalculator;
