import React, { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import ClockFace from './clock/ClockFace';

const RetinalCalculator = () => {
  const [age, setAge] = useState('');
  const [pvrGrade, setPvrGrade] = useState('A');
  const [selectedHours, setSelectedHours] = useState([]);
  const [detachmentSegments, setDetachmentSegments] = useState([]);

  // Constants from R code
  const coefficients = {
    constant: -2.391,
    ageGroups: {
      'age 65 to 79': 0.841,
      'age ≥80': 1.137,
      'age <45': -0.841,
      'Other': 0
    },
    pvrGrade: {
      'C': 1.619,
      'A': 0,
      'B': 0
    }
  };

  // Helper function to get age group
  const getAgeGroup = (age) => {
    if (!age) return 'Other';
    const ageNum = parseInt(age);
    if (ageNum < 45) return 'age <45';
    if (ageNum >= 80) return 'age ≥80';
    if (ageNum >= 65) return 'age 65 to 79';
    return 'Other';
  };

  // Helper function to analyze break locations
  const analyzeBreaks = () => {
    const breaks = selectedHours;
    const has48 = breaks.includes(4) || breaks.includes(8);
    const has567 = breaks.includes(5) || breaks.includes(6) || breaks.includes(7);
    const hasNoBreak = breaks.length === 0;

    return {
      break48: has48 ? 0.405 : 0,
      break567: has567 ? 0.619 : 0,
      noBreak: hasNoBreak ? 1.099 : 0
    };
  };

  // Helper function to analyze detachment location
  const analyzeDetachment = () => {
    const affectedHours = Array.from(new Set(detachmentSegments.map(seg => 
      Math.floor(seg / 5) + 1
    )));

    const has345 = [3,4,5].some(h => affectedHours.includes(h));
    const has6 = affectedHours.includes(6);
    const isTotal = affectedHours.length >= 10; // Approximate for total detachment

    return {
      three_five: has345 ? 0.560 : 0,
      six: has6 ? 0.993 : 0,
      total: isTotal ? 1.078 : 0
    };
  };

  // Calculate risk
  const calculateRisk = () => {
    let logit = coefficients.constant;

    // Add age coefficient
    logit += coefficients.ageGroups[getAgeGroup(age)];

    // Add PVR coefficient
    logit += coefficients.pvrGrade[pvrGrade];

    // Add break location coefficients
    const breakEffects = analyzeBreaks();
    logit += breakEffects.break48;
    logit += breakEffects.break567;
    logit += breakEffects.noBreak;

    // Add detachment location coefficients
    const detachmentEffects = analyzeDetachment();
    logit += detachmentEffects.three_five;
    logit += detachmentEffects.six;
    logit += detachmentEffects.total;

    // Convert logit to probability
    const probability = 1 / (1 + Math.exp(-logit));
    return (probability * 100).toFixed(1);
  };

  const handleClockChange = ({ tears, detachment }) => {
    setSelectedHours(tears);
    setDetachmentSegments(detachment);
  };

  return (
    <div className="flex flex-col space-y-4 p-4">
      <Card>
        <CardHeader>
          <CardTitle>Retinal Detachment Risk Calculator</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <label className="block text-sm font-medium">Age (years)</label>
            <input 
              type="number"
              className="w-full p-2 border rounded"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              min="0"
              max="120"
              placeholder="Enter age"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-medium">PVR Grade</label>
            <select 
              className="w-full p-2 border rounded"
              value={pvrGrade}
              onChange={(e) => setPvrGrade(e.target.value)}
            >
              <option value="A">Grade A</option>
              <option value="B">Grade B</option>
              <option value="C">Grade C</option>
            </select>
          </div>

          <div className="mt-4">
            <ClockFace
              selectedHours={selectedHours}
              detachmentSegments={detachmentSegments}
              onChange={handleClockChange}
            />
          </div>

          <div className="mt-6 p-4 bg-gray-100 rounded-lg">
            <p className="text-lg font-semibold">
              Estimated Risk of Failure: {calculateRisk()}%
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default RetinalCalculator;
