import React from 'react';
import Controls from './Controls';
import ClockFace from './ClockFace';
import { useClockInteractions } from './hooks/useClockInteractions';

const MinimalClockSelector = ({ onChange }) => {
  const {
    selectedHours,
    detachmentSegments,
    hoveredHour,
    setHoveredHour,
    isAddMode,
    setIsAddMode,
    isDrawing,
    isTouchDevice,
    handleTearTouchMove,
    handleTearTouchEnd,
    handleSegmentInteraction,
    handleStartDrawing,
    handleDrawing,
    handleMouseDown,
    handleTearClick,
    handleTearTouchStart,
    handleEndDrawing,
    handleClearAll
  } = useClockInteractions(onChange);

  const handlers = {
    setHoveredHour,
    handleTearTouchMove,
    handleTearTouchEnd,
    handleSegmentInteraction,
    handleStartDrawing,
    handleDrawing,
    handleMouseDown,
    handleTearClick,
    handleTearTouchStart,
    handleEndDrawing
  };

  return (
    <div className="w-full min-h-screen p-4 bg-gray-50">
      <div className="max-w-7xl mx-auto">
        <div className="flex flex-col items-center gap-4">
          {/* Clock section */}
          <div className="w-full flex justify-center">
            <div className="w-full max-w-2xl">
              <ClockFace
                selectedHours={selectedHours}
                detachmentSegments={detachmentSegments}
                hoveredHour={hoveredHour}
                isTouchDevice={isTouchDevice}
                handlers={handlers}
              />
            </div>
          </div>

          {/* Controls - now positioned below clock */}
          <div className="w-full flex justify-center landscape:justify-end md:justify-end">
            <Controls
              isTouchDevice={isTouchDevice}
              isAddMode={isAddMode}
              setIsAddMode={setIsAddMode}
              isDrawing={false} 
              handleClearAll={handleClearAll}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MinimalClockSelector;
