import React, { useState } from 'react';
import ClockFace from './clock/ClockFace';
import RiskInputForm from './RiskInputForm';
import RiskResults from './RiskResults';
import { calculateRiskWithSteps } from '../utils/riskCalculations';
import { ClockHourNotation } from 'src/components/clock/utils/clockHourNotation.js';
const RetinalCalculator = () => {
    // State management with defaults
    const [age, setAge] = useState('');
    const [pvrGrade, setPvrGrade] = useState('none');  // default to "No PVR"
    const [vitrectomyGauge, setVitrectomyGauge] = useState('25g');  // default to "25 gauge"
    const [selectedHours, setSelectedHours] = useState([]);
    const [detachmentSegments, setDetachmentSegments] = useState([]);
    const [hoveredHour, setHoveredHour] = useState(null);
    const [showMath, setShowMath] = useState(false);
    const [calculatedRisk, setCalculatedRisk] = useState(null);

    // Common props for RiskInputForm
    const formProps = {
        age,
        setAge,
        pvrGrade,
        setPvrGrade,
        vitrectomyGauge,
        setVitrectomyGauge
    };

    const handleHoverChange = (hour) => {
        setHoveredHour(hour);
    };

    const handleTearToggle = (hour) => {
        const newSelection = selectedHours.includes(hour)
            ? selectedHours.filter(h => h !== hour)
            : [...selectedHours, hour];
        setSelectedHours(newSelection);
    };

    const handleSegmentToggle = (segment) => {
        const newDetachment = detachmentSegments.includes(segment)
            ? detachmentSegments.filter(s => s !== segment)
            : [...detachmentSegments, segment];
        setDetachmentSegments(newDetachment);
    };

    const handleCalculate = () => {
        if (!age || detachmentSegments.length === 0) return;

        const risk = calculateRiskWithSteps({
            age,
            pvrGrade,
            vitrectomyGauge,
            selectedHours,
            detachmentSegments
        });
        setCalculatedRisk(risk);
    };

    const handleReset = () => {
        setAge('');
        setPvrGrade('none'); // Reset to "No PVR"
        setVitrectomyGauge('25g'); // Reset to "25 gauge"
        setSelectedHours([]);
        setDetachmentSegments([]);
        setCalculatedRisk(null);
        setShowMath(false);
    };

    const isCalculateDisabled = !age || detachmentSegments.length === 0;

    const formatHoursList = (hours) => {
        if (hours.length === 0) return 'None';
        return hours.sort((a, b) => a - b).join(', ') + " o'clock";
    };

    // Helper function to convert segment to clock hour
    const segmentToHour = (segment) => {
        const hour = Math.floor(segment / 5) % 12;
        return hour === 0 ? 12 : hour;
    };


    const formatDetachmentHours = (segments) => {
        if (segments.length === 0) return 'None';

        if (segments.length >= 55) {
            return "1-12 o'clock";
        }

        const hours = new Set();

        const touchesHour = (segment, hour) => {
            if (hour === 11) {
                return segment >= 50 || segment >= 55;
            }
            if (hour === 12) {
                return segment >= 55 || segment <= 4;
            }
            if (hour === 1) {
                return segment >= 0 && segment <= 9;
            }
            const startSegment = (hour - 1) * 5;
            const endSegment = (hour * 5) - 1;
            return segment >= startSegment && segment <= endSegment;
        };

        // Convert segments to hours
        for (let hour = 1; hour <= 12; hour++) {
            if (segments.some(segment => touchesHour(segment, hour))) {
                hours.add(hour);
            }
        }

        if (hours.has(5) || hours.has(7)) {
            hours.add(6);
        }

        if (segments.some(segment => segment >= 10 && segment <= 14) || 
            segments.some(segment => segment >= 20 && segment <= 24)) {
            hours.add(3);
        }

        if (segments.some(segment => segment >= 40 && segment <= 44) || 
            segments.some(segment => segment >= 50 && segment <= 54)) {
            hours.add(9);
        }

        // Convert to array and pre-process for midnight crossing
        const hourList = Array.from(hours);

        // Check for midnight crossing patterns
        const hasMidnight = hours.has(12);
        const hasOne = hours.has(1);
        const hasTwo = hours.has(2);
        const hasEleven = hours.has(11);

        // Sort with midnight crossing logic
        hourList.sort((a, b) => {
            if (hasMidnight && (hasOne || hasTwo)) {
                // Special handling for midnight crossing sequence
                const getValue = (h) => {
                    if (h === 12) return 0;  // Place 12 at start of midnight sequence
                    if (h === 1) return 1;
                    if (h === 2) return 2;
                    if (h === 11) return -1;
                    return h + 2;  // Other numbers shifted to maintain order
                };
                return getValue(a) - getValue(b);
            }
            return a - b;
        });

