import React from 'react';
import Controls from './Controls';
import ClockFace from './ClockFace';
import { useClockInteractions } from './hooks/useClockInteractions';

const MinimalClockSelector = ({ onChange }) => {
  const {
    selectedHours,
    detachmentSegments,
    hoveredHour,
    setHoveredHour,
    isAddMode,
    setIsAddMode,
    isDrawing,
    isTouchDevice,
    handleTearTouchMove,
    handleTearTouchEnd,
    handleSegmentInteraction,
    handleStartDrawing,
    handleDrawing,
    handleMouseDown,
    handleTearClick,
    handleTearTouchStart,
    handleEndDrawing,
    handleClearAll
  } = useClockInteractions(onChange);

  const handlers = {
    setHoveredHour,
    handleTearTouchMove,
    handleTearTouchEnd,
    handleSegmentInteraction,
    handleStartDrawing,
    handleDrawing,
    handleMouseDown,
    handleTearClick,
    handleTearTouchStart,
    handleEndDrawing
  };

  return (
    <div className="w-full min-h-screen p-4 bg-gray-50">
      <div className="max-w-7xl mx-auto relative">
        {/* Main content wrapper with flexible layout */}
        <div className="flex flex-col md:flex-row items-center md:items-start gap-4 relative">
          {/* Controls section - positioned differently based on screen size and orientation */}
          <div className="order-2 md:order-1 fixed md:static bottom-4 left-1/2 transform -translate-x-1/2 md:translate-x-0 md:bottom-auto md:left-auto landscape:static landscape:bottom-auto landscape:left-auto landscape:transform-none md:ml-auto">
            <Controls
              isTouchDevice={isTouchDevice}
              isAddMode={isAddMode}
              setIsAddMode={setIsAddMode}
              isDrawing={false}
              handleClearAll={handleClearAll}
            />
          </div>

          {/* Clock section */}
          <div className="order-1 md:order-2 w-full max-w-2xl">
            <ClockFace
              selectedHours={selectedHours}
              detachmentSegments={detachmentSegments}
              hoveredHour={hoveredHour}
              isTouchDevice={isTouchDevice}
              handlers={handlers}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MinimalClockSelector;
